def move(start, instructions):
    landing_floor = start
    for i in instructions:
        if i == "(":
            landing_floor += 1
        elif i == ")":
            landing_floor -= 1

    return landing_floor


def basement_position(start, instructions):
    landing_floor = start
    for i in range(len(instructions)):
        if instructions[i] == "(":
            landing_floor += 1
        elif instructions[i] == ")":
            landing_floor -= 1

        if landing_floor == -1:
            # Response is not in 0-based index
            return i + 1


